    .appendTo($overlay);

  var $close = $('<button>', { text:'X', title:'Close' })
    .css({ position:'fixed', top:'14px', right:'16px', zIndex:1000000, width:'36px', height:'36px', border:'none', borderRadius:'18px', background:'rgba(255,255,255,.28)', color:'#fff', fontSize:'20px', fontWeight:'700', cursor:'pointer' })
